class Solution {
public:
    bool helper(int i, int sum, int k, int ind, vector<vector<int>>& q, vector<vector<int>>& dp) {
        if (sum < 0) return false;
        if (sum == 0) return true;
        if (i == k) return false;
        if (dp[i][sum] != -1) return dp[i][sum];

        bool ans = helper(i + 1, sum, k, ind, q, dp);
        if (ind >= q[i][0] && ind <= q[i][1]) 
            ans |= helper(i + 1, sum - q[i][2], k, ind, q, dp);

        return dp[i][sum] = ans;
    }

    bool check(int k, vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        vector<int> v(n + 1, 0);

        // Apply the first `k` queries using prefix sum
        for (int i = 0; i < k; i++) {
            int l = queries[i][0], r = queries[i][1], val = queries[i][2];
            v[l] += val;
            if (r + 1 < n) v[r + 1] -= val;
        }

        // Compute the modified array
        for (int i = 1; i < n; i++) {
            v[i] += v[i - 1];
        }

        // Check if each element can be made zero using at most `k` operations
        for (int i = 0; i < n; i++) {
            int x = v[i] - nums[i];
            if (x < 0) return false;

            vector<vector<int>> dp(k + 1, vector<int>(x + 1, -1));
            if (!helper(0, x, k, i, queries, dp)) return false;
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int q = queries.size();
        int l = 0, r = q, ans = -1;

        while (l <= r) {
            int mid = l + (r - l) / 2;
            if (check(mid, nums, queries)) {
                ans = mid;
                r = mid - 1;
            } else {
                l = mid + 1;
            }
        }
        return ans;
    }
};

