class Solution:
    def recurse(self, ix, num, q_ix, queries, dp):
        if num == 0:
            print(num, q_ix)
            return q_ix
        if q_ix == len(queries):
            return None
        if (ix, num, q_ix) in dp:
            return dp[(ix, num, q_ix)]
        # Can we use this current query?
        query = queries[q_ix]
        k1 = None
        if (query[0] <= ix <= query[1]) and num >= query[2]:
            # Consider recursing down this path
            print("Recursing down", num)
            k1 = self.recurse(ix, num-query[2], q_ix+1, queries, dp)
        # Or just ignore it
        k2 = self.recurse(ix, num, q_ix+1, queries, dp)
        if k1 is None:
            ans = k2
        elif k2 is None:
            ans = k1
        else:
            ans = min(k1, k2)
        dp[(ix, num, q_ix)] = ans
        return ans

    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        ans = 0

        # Maybe simulation?
        # For each num, check how many queries it takes to get to 0?
        # Extremely slow
        for i, num in enumerate(nums):
            k = self.recurse(i, num, 0, queries, {})
            if k == None:
                return -1
            ans = max(ans, k)
        return ans

