class Solution {
public:
    std::vector<std::unordered_set<int>> possible_values;
    std::vector<int> done;
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        auto N = nums.size();
        done.resize(N, 0);
        possible_values.resize(N, {});
        for (size_t i = 0; i < N; i++) {
            if (nums[i] == 0) {
                done[i] = true;
                continue;
            }
            possible_values[i].insert(nums[i]);
        }
        if (std::all_of(done.begin(), done.end(), [](auto v) {return v;})) {
            return 0;
        }
        for (auto i = 0; i < queries.size(); i++) {
            auto range_l = queries[i][0];
            auto range_r = queries[i][1];
            auto decrement_val = queries[i][2];
            for (auto j = range_l; j <= range_r; j++) {
                if (done[j]) continue;
                auto& pv = possible_values[j];
                if (pv.count(decrement_val)) {
                    done[j] = true;
                } else {
                    std::vector<int> decreased;
                    for (auto v : pv) {
                        decreased.push_back(v - decrement_val);
                    }
                    pv.insert(decreased.begin(), decreased.end());
                }
            }
            if (std::all_of(done.begin(), done.end(), [](auto v) {return v;})) {
                return i + 1;
            }
        }
        return -1;
    }
};

