#define INF 1e5
class Solution {
public:
    int dfs(int i, int j, int sum, vector<vector<int>>& queries,
        vector<vector<int>>& dp) {
        int m = queries.size();
        if (sum == 0) {
            return j;
        }
        if (sum < 0 || j == m) {
            return INF;
        }
        int& res = dp[j][sum];
        if (res != INT_MAX) {
            return res;
        }
        res = INF;
        if (i >= queries[j][0] && i <= queries[j][1]) {
            res = dfs(i, j+1, sum-queries[j][2], queries, dp);
        }
        res = min(res, dfs(i, j+1, sum, queries, dp));
        return res;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        int ans = -1;
        vector<vector<int>> dp;
        for (int i = 0; i < n; i++) {
            dp.assign(m, vector<int>(1e3+1, INT_MAX));
            int res = dfs(i, 0, nums[i], queries, dp);
            if (res < INF) {
                ans = max(ans, res);
            } else {
                return -1;
            }
        }
        return ans;
    }
};

