class Solution {
public:
    vector<vector<int>> dp;

    int solve(int i, int curr, int idx, vector<vector<int>>& queries) {
        if (curr == 0) return i;
        if(curr<0) return INT_MAX;
        if (i >= queries.size()) return INT_MAX;

        if (dp[i][curr] != -1) return dp[i][curr];

        int l = queries[i][0], r = queries[i][1], val = queries[i][2];
        int opt1 = INT_MAX;

        if (idx >= l && idx <= r) opt1 = solve(i + 1,curr-val, idx, queries);
        opt1 = min(opt1, solve(i + 1, curr, idx, queries));

        return dp[i][curr] = opt1;
    }

    int minZeroArray(vector<int>& arr, vector<vector<int>>& queries) {
        int ans = -1;

        for (int i = 0; i < arr.size(); i++) {
            dp.assign(queries.size(), vector<int>(1001, -1));
            int currans = solve(0, arr[i], i, queries);
            if (currans == INT_MAX) return -1;
            ans = max(ans, currans);
        }

        return ans;
    }
};

