class Solution {
public:
    bool isGood(int val , vector<int> &values , int idx, vector<vector<int>> & dp){
        if (dp[val][idx] != -1)return dp[val][idx] ; 
        if (val == 0){
            return true ; 
        }
        if (idx >= values.size()){
            return false ; 
        }
        bool take = 0 , notTake = 0 ; 
        notTake = isGood(val , values , idx + 1 , dp);
        if (values[idx] <= val){
            take = isGood(val - values[idx] , values ,idx + 1 , dp) ;
        }
        return dp[val][idx] = (take || notTake) ; 
    }
    bool isOk(int limit , vector<int> & nums , vector<vector<int>> & queries){
        int n = nums.size();
        vector<vector<int>>mp(n);
        for (int i = 0 ; i < limit ; i++ ){
            int l = queries[i][0] , r = queries[i][1] , val = queries[i][2];
            for (int j = l ; j <= r ; j++){
                mp[j].push_back(val);
            }
        }
        for (int i = 0 ; i < mp.size() ; i++ ){
            int my_val = nums[i];
            vector<vector<int>>dp(my_val + 1,vector<int>( mp[i].size() + 1 , -1));
            if(isGood(my_val ,mp[i] , 0 , dp)){
                continue;
            }else{
                return false;
            }
        }
        return true ; 
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int low = 1 , high = queries.size() ; 
        int ans = -1 ; 
        int n = nums.size();
        bool check = false ; 
        for (int i= 0 ; i < n ; i++){
            if (nums[i] != 0)check = true ; 
        }
        if (check == 0)return 0 ; 
        while (low <= high){
            int mid = (low + high)>>1 ; 
            if(isOk(mid , nums , queries)){
                ans = mid ; 
                high = mid - 1 ;
            }else{
                low = mid + 1 ; 
            }
        }
        return ans;
    }
};

