class Solution {
    public:
        bool f(int i, int target, vector<int>&c , vector<vector<int>>&dp){

            if(target == 0){
                return true;
            }

            if(i >= c.size()){
                return false;
            }

            if(dp[i][target] != -1){
                return dp[i][target];
            }

            bool res = f(i+1, target, c, dp) || (c[i] <= target && f(i+1, target-c[i], c, dp));
            dp[i][target] = res;
            return res;
        }

        int minZeroArray(vector<int>&nums, vector<vector<int>>&queries){

            int n = nums.size();
            int m = queries.size();

            bool iszero = true;
            for(int num : nums){
                if(num != 0){
                    iszero = false; 
                    break; 
                }
            }
            if(iszero){
                return 0;
            } 

            for(int k = 1; k <= m; k++){
                bool allzero = true;
                for(int j = 0; j < n; j++){
                    vector<int>c;
                    for(int i = 0; i < k; i++){
                        if(queries[i][0] <= j && j <= queries[i][1])
                            c.push_back(queries[i][2]);
                    }
                    vector<vector<int>>dp(c.size()+1, vector<int>(nums[j]+1,-1));
                    if(!f(0, nums[j], c, dp)){
                        allzero = false;
                        break;
                    }
                }
                if(allzero){
                    return k;
                }
            }

            return -1;
        }
    };

