class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        def can_zero(k):
            for i in range(len(nums)):
                available_vals = [val for l, r, val in queries[:k] if l <= i <= r]
                target = nums[i]
                dp = [False] * (target + 1)
                dp[0] = True
                for val in available_vals:
                    for j in range(target, val - 1, -1):
                        dp[j] |= dp[j - val]
                if not dp[target]:
                    return False
            return True

        left, right = 0, len(queries)
        result = -1


        while left <= right:
            mid = (left + right) // 2
            if can_zero(mid):
                result = mid
                right = mid - 1
            else:
                left = mid + 1

        return result

