class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n=len(nums)
        m=len(queries)
        if sum(nums)==0:return 0
        dic=defaultdict(list)
        for i in range(m):
            l,r,v=queries[i]
            for j in range(l,r+1):
                dic[j].append([v,i+1])
        st=[-1]*n
        for i in dic:
            c=nums[i]
            d=[0]*(c+1)
            d[0]=1
            for v,k in dic[i]:
                for j in range(c,v-1,-1):
                    if d[j-v]:
                        d[j]=1
                if d[c]:
                    st[i]=k
                    break
        for i in range(n):
            if st[i]==-1:
                return -1
        return max(st)

