        if all(x == 0 for x in a):
            return 0
        for i, (l, r, v) in enumerate(q):
            for j in range(l, r + 1):
                S[j] |= {x + v for x in S[j] if x + v <= a[j]}
            if all(a[j] in S[j] for j in range(n)):
                return i + 1
        return -1

