class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {

        ArrayList<Integer>[] arr = new ArrayList[11];
        int qlen = queries.length;
        for(int i = 0; i < arr.length; i++){
            arr[i] = new ArrayList<>();
        }
        int z = 0;
        for(int i = 0; i < nums.length; i++){
            if(nums[i] > 0){
                z = 1;
                break;
            }
        }
        if(z == 0){
            return 0;
        }
        for(int i = 0; i < qlen; i++){
            int l = queries[i][0];
            int r = queries[i][1];
            int val = queries[i][2];
            for(int j = l; j<= r; j++){
                ArrayList<Integer> temp = arr[j];
                temp.add(val);
            }
            int flag = 0;
            for(int j = 0; j <nums.length; j++){
                ArrayList<Integer> temp = arr[j];
                int check = targetSum(temp, nums[j]);
                if(check == 0){
                    flag = 1;
                    break;
                }
            }
            if(flag == 0){
                return i + 1;
            }

        }
        return -1;
    }
     private int targetSum(ArrayList<Integer> list, int target) {
        if (list.isEmpty()) return 0;

        int sum = 0;
        for (int num : list) sum += num;
        if (sum < target) return 0;

        int[] dp = new int[target + 1];
        dp[0] = 1;

        for (int num : list) {
            for (int j = target; j >= num; j--) {
                dp[j] |= dp[j - num];
            }
        }
        return dp[target];
    }
}

