class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
         int n = nums.length, m = queries.length;
        int[] arr = Arrays.copyOf(nums, n);
        int l = 0, h = m, result = -1;
        while (l <= h) {
            int mid = (l + h) / 2;
            if (check(n,mid,queries,nums)) {
                result = mid;
                h = mid - 1;
            } else {
                l = mid + 1;
            }
        }
        return result;
    }
    public boolean check(int n,int k,int[][] queries,int nums[])
    {

            for (int j = 0; j < n; j++) {
                boolean[] dp = new boolean[1001];
                dp[0] = true;
                for (int i = 0; i < k; i++) {
                    if (j >= queries[i][0] && j <= queries[i][1]) {
                        for (int sum = 1000 - queries[i][2]; sum >= 0; sum--) {
                            if (dp[sum]) dp[sum + queries[i][2]] = true;
                        }
                    }
                }
                if (!dp[nums[j]]) return false;
            }
            return true;
    }
}

