class Solution {
public:
    bool check(vector<int>& nums, vector<vector<int>>& queries, int k) {
        for (int j = 0; j < nums.size(); j++) {
            vector<int> temp;
            for (int i = 0; i < k; i++) {
                if (queries[i][0] <= j && j <= queries[i][1]) {
                    temp.push_back(queries[i][2]);
                }
            }
            bool flag = true;
            vector<bool> dp(nums[j] + 1, false);
            dp[0] = true;
            for (int i=0;i<temp.size();i++) {
                for (int s = nums[j]; s >= temp[i]; s--) {
                    if (dp[s - temp[i]]) {
                        dp[s] = true;
                    }
                }
            }
            flag= dp[nums[j]];
            if (!flag) {
                return false;
            }
        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int q = queries.size();
        bool flag = true;
        for (int i = 0; i < n; i++) {
            if (nums[i] != 0) {
                flag = false;
                break;
            }
        }
        if (flag) {
            return 0;
        }
        int start = 1, end = q + 1, ans = -1;
        while (start < end) {
            int mid = start + (end - start) / 2;
            if (check(nums, queries, mid)) {
                ans = mid;
                end = mid;
            } else {
                start = mid + 1;
            }
        }
        return ans;
    }
};

