        int dp[10][1010];
        int ans[10] = {-1};
        memset(dp, 0, sizeof dp);
        memset(ans,-1,sizeof ans);
        for(int i = 0; i < nums.size(); ++i){
            dp[i][0] = 1;
        }
        vector<int>tmp = {-1,-1,0};
        queries.insert(queries.begin(),tmp);
        for(int i = 0; i < nums.size(); ++i){
            //cout<<"nums"<<i<<endl;
            if(!nums[i]){
                //cout<<"ans "<<i<<' '<<ans[i]<<'n';
                ans[i] = 0;
                continue;
            }
            for(int j = 1; j < queries.size() && !~ans[i]; ++j)
                if(i >= queries[j][0] && i <= queries[j][1])
                    for(int k = nums[i] - queries[j][2]; k >= 0 && !~ans[i]; --k){
                        dp[i][k + queries[j][2]] = max(dp[i][k + queries[j][2]],dp[i][k]);
                        //cout<<i<<' '<<k + queries[j][2]<<' '<<dp[i][k + queries[j][2]]<<endl;
                        if(dp[i][nums[i]]){
                            ans[i] = j;
                            //cout<<"ans "<<i<<' '<<ans[i]<<'n';
                        }
                    }
        }
        int ret = 0;
        //for(int i = 0; i<nums.size();++i)
          //  cout<<i<<' '<<ans[i]<<endl;
        for(int i = 0; i < nums.size(); ++i)
            if(!~ans[i])
                return -1;
            else
                ret = max(ret, ans[i]);
        return ret;
    }
};

