class Solution {
public:
    int f(int x , int ind , vector<vector<int>> &dp,vector<vector<int>>& queries){
        int m = queries.size();
        for(int i = m-1;i>=0;i--){
            for(int j = x ; j>=1 ; j--){
                int one  = dp[i+1][j];
                int two = 1e9;
                if(ind >= queries[i][0] && ind <= queries[i][1] && j>=queries[i][2]){
                    two = dp[i+1][j-queries[i][2]];
                }
                dp[i][j] = 1 + min(one,two);
            }
        }
        return dp[0][x];
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        vector<vector<int>> dp(m+1,vector<int>(1001,1e9));
        for(int i = 0 ; i<=m;i++){
            dp[i][0] = 0;
        }
        int maxi = 0;
        for(int i = 0 ;i<nums.size() ;i++){
            int ans = f(nums[i],i,dp,queries);
            if(ans >= 1e9) return -1;
            maxi = max(maxi,ans);
        }
        return maxi;
    }
};

