        int[][] prevDp = new int[n][MAX_VAL + 1];
        int[][] currDp = new int[n][MAX_VAL + 1];
        for(int i = 0; i < n; i++) Arrays.fill(prevDp[i], -1);
        for(int i = 0; i < n; i++) prevDp[i][0] = 1;
        for(int queryNo = 0; queryNo < q; queryNo++){
            for(int i = 0; i < n; i++) {
                for(int col = 0; col <= MAX_VAL; col++) currDp[i][col] = prevDp[i][col];
            }
            int left = queries[queryNo][0], right = queries[queryNo][1], val = queries[queryNo][2];
            for(int idx = left; idx <= right; idx++){
                int[] currRow = currDp[idx], prevRow = prevDp[idx];
                if(val <= MAX_VAL) currRow[val] = 1;
                currRow[0] = 1;
                for(int col = 0; col <= MAX_VAL; col++){
                    if(col - val >= 0 && prevRow[col - val] == 1) currRow[col] = 1; 
                    if(prevRow[col] == 1) currRow[col] = 1; 
                }
            }

            // System.out.println("queryNo: " + queryNo);
            // for(int i = 0; i < n; i++){
            //     for(int j = 0; j <= MAX_VAL; j++) System.out.print(currDp[i][j] + " ");
            //     System.out.println("");
            // }
            // System.out.println("n-----------");

            int flag = 0;
            for(int i = 0; i < n; i++){
                if(currDp[i][arr[i]] == -1) {
                    flag = 1;
                    break;
                }
            }
            if(flag == 0) return queryNo + 1;
            for(int row = 0; row < n; row++){
                for(int col = 0; col <= MAX_VAL; col++) prevDp[row][col] = currDp[row][col];
            }
        }
        return -1;
    }
}

