int solve(vvi & dp, int k, int ind, vvi & queries, int ptr = 0, int cur = 0){
    if(cur == k) return ptr;
    if(cur > k) return INT_MAX;
    if(ptr>=queries.size()) return INT_MAX;
    if(dp[cur][ptr] != -1) return dp[cur][ptr];
    int left = queries[ptr][0], right = queries[ptr][1], val = queries[ptr][2];
    int pick = INT_MAX;
    if(left <=ind && right >= ind){
        pick = min(pick , solve(dp, k, ind, queries, ptr+1, cur + val));
    }
    int skip = solve(dp, k, ind, queries, ptr+1, cur);
    return dp[cur][ptr] = min(pick, skip);
}

class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ans = 0;
        for(int i = 0; i<nums.size(); i++){
            vvi dp(nums[i]+1, v<int>(queries.size(), -1));
            int cur = solve(dp, nums[i], i, queries);
            if(cur > queries.size()) return -1;
            ans = max(ans, cur);
        }
        return ans;
    }
};

