class Solution
{

private:
    bitset<1001> ss(const bitset<1001> &bitSet, int shift)
    {
        bitset<1001> shifted;
        for (int i = 0; i + shift < 1001; i++)
        {
            if (bitSet.test(i))
            {
                shifted.set(i + shift);
            }
        }
        return shifted;
    }

public:
    int minZeroArray(vector<int> &array, vector<vector<int>> &operations)
    {
        int length = array.size();
        vector<bitset<1001>> mp(length);

        for (int i = 0; i < length; i++)
        {
            mp[i].set(0, true);
        }

        bool zero = true;
        for (int value : array)
        {
            if (value != 0)
            {
                zero = false;
                break;
            }
        }
        if (zero)
        {
            return 0;
        }

        for (int op = 0; op < operations.size(); op++)
        {
            int start = operations[op][0];
            int end = operations[op][1];
            int increment = operations[op][2];

            for (int i = start; i <= end; i++)
            {
                bitset<1001> temp = ss(mp[i], increment);
                mp[i] |= temp;
                for (int pos = array[i] + 1; pos < 1001; pos++)
                {
                    mp[i].reset(pos);
                }
            }

            bool istrue = true;
            for (int i = 0; i < length; i++)
            {
                if (!mp[i].test(array[i]))
                {
                    istrue = false;
                    break;
                }
            }

            if (istrue)
                return op + 1;
        }

        return -1;
    }
};

