class Solution {
public:
bool r(int i, int s, int t, int j, int k, const vector<vector<int>> &q, vector<vector<int>> &m) {
    if(s == t) return true;
    if(i == k) return false;
    if(s > t) return false;
    if(m[i][s] != -1) return m[i][s];
    bool res = false;
    if(j >= q[i][0] && j <= q[i][1])
        res = r(i + 1, s, t, j, k, q, m) || ((s + q[i][2] <= t) && r(i + 1, s + q[i][2], t, j, k, q, m));
    else
        res = r(i + 1, s, t, j, k, q, m);
    m[i][s] = res;
    return res;
}

bool chk(int j, int k, const vector<int> &ns, const vector<vector<int>> &q) {
    int t = ns[j];
    vector<vector<int>> m(k + 1, vector<int>(t + 1, -1));
    return r(0, 0, t, j, k, q, m);
}

bool pos(int k, const vector<int> &ns, const vector<vector<int>> &q) {
    int n = ns.size();
    for(int i = 0; i < n; i++)
        if(!chk(i, k, ns, q)) return false;
    return true;
}

int minZeroArray(vector<int>& ns, vector<vector<int>>& q) { 
    int m = q.size();
    int ans = -1; 
    int hi = m, lo = 0;
    int n = ns.size();
    while(lo <= hi) {
        int mid = lo + (hi - lo) / 2;
        if(pos(mid, ns, q)) { ans = mid; hi = mid - 1; }
        else lo = mid + 1;
    }
    return ans;
}
};

