class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if sum(nums) == 0:
            return 0
        def is_value_sum(aa, tt):
            @lru_cache(maxsize=None)
            def find(cur, ii):
                # print(cur, ii, 'here')
                if cur == 0:
                    return True
                if ii < 0:
                    return False
                vv = aa[ii]
                skip = find(cur, ii - 1)
                if vv > cur:
                    return skip
                skip = skip or find(cur - vv, ii - 1)
                return skip

            # print(tt, aa, find(tt, len(aa) - 1))
            return find(tt, len(aa) - 1)



        def is_all_zero(kk):
            # print('is_all_zero', kk)
            tt = {}
            for ii, num in enumerate(nums):
                tt[ii] = []
                for jj in range(kk + 1):
                    ll, rr, vv = queries[jj]
                    if ii not in range(ll, rr + 1):
                        continue
                    tt[ii].append(vv)
            for ii in range(len(nums)):
                tt[ii].sort()
                if is_value_sum(tt[ii], nums[ii]):
                    continue
                return False
            return True

        def bisect_search(ll, rr):
            # print('bisect', ll, rr)
            if ll == rr:
                if is_all_zero(ll):
                    return ll + 1
                return -1
            mid = (ll + rr) // 2
            if is_all_zero(mid):
                return bisect_search(ll, mid)
            return bisect_search(mid + 1, rr)
        return bisect_search(0, len(queries) - 1)

