class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        // Check if the array is already all zeros
        bool allZero = true;
        for (int num : nums) {
            if (num != 0) {
                allZero = false;
                break;
            }
        }
        if (allZero) return 0;

        int n = nums.size();
        int m = queries.size();

        // Preprocess: For each element, collect the queries that include it
        vector<vector<pair<int, int>>> elementQueries(n);
        for (int i = 0; i < m; ++i) {
            auto& query = queries[i];
            int l = query[0], r = query[1], val = query[2];
            for (int j = l; j <= r; ++j) {
                elementQueries[j].emplace_back(i, val);
            }
        }

        int maxK = 0;
        for (int i = 0; i < n; ++i) {
            int target = nums[i];
            if (target == 0) continue;

            unordered_set<int> possibleSums;
            possibleSums.insert(0);
            int currentK = -1;
            bool found = false;

            for (const auto& [queryIdx, val] : elementQueries[i]) {
                unordered_set<int> newSums;
                for (int sum : possibleSums) {
                    // Option to not include the current query's value
                    newSums.insert(sum);
                    // Option to include the current query's value
                    int newSum = sum + val;
                    if (newSum <= target) {
                        newSums.insert(newSum);
                    }
                }
                possibleSums = move(newSums);

                if (possibleSums.find(target) != possibleSums.end()) {
                    currentK = queryIdx + 1; // Convert to 1-based index
                    found = true;
                    break;
                }
            }

            if (!found) return -1;
            if (currentK > maxK) maxK = currentK;
        }

        return maxK;
    }
};

