class Solution {
public:
    int calc(int n,int ind,vector<int>& v,vector<vector<int>>& dp){
        if(n==0) return ind;
        if(ind==v.size()) return INT_MAX;
        if(dp[n][ind]!=-1) return dp[n][ind];
        int ans=INT_MAX;
        if(v[ind]<=n)
        ans=min(ans,calc(n-v[ind],ind+1,v,dp));
        ans=min(ans,calc(n,ind+1,v,dp));
        return dp[n][ind]=ans;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size(),m=queries.size();
        vector<vector<int>> tab(n);
        map<pair<int,int>,int> mp;
        for(int i=0;i<m;i++){
            for(int j=queries[i][0];j<=queries[i][1];j++){
                mp[{j,0}]=0;
                tab[j].push_back(queries[i][2]);
                mp[{j,tab[j].size()}]=i+1;
            }
        }
        int ans=-1;
        for(int i=0;i<n;i++){
            vector<vector<int>> dp(nums[i]+10,vector<int>(tab[i].size()+10,-1));
            int temp=calc(nums[i],0,tab[i],dp);
            if(temp==INT_MAX) return -1;
            ans=max(ans,mp[{i,temp}]);
        }
        return ans;
    }
};

