class Solution {
    int can_zero(int x, vector<pair<int, int>> &v) {
        if (x == 0) {
            return 0;
        }
        int n = v.size();
        vector<vector<int>> dp(n + 1, vector<int> (x + 1, 0));
        dp[0][0] = 1;
        for (int i = 1; i <= n; i++) {
            for (int j = 0; j <= x; j++) {
                dp[i][j] = dp[i - 1][j];
                if (j >= v[i - 1].second) {
                    dp[i][j] = dp[i][j] || dp[i - 1][j - v[i - 1].second];
                }
            }
            if (dp[i][x]) {
                return v[i - 1].first + 1;
            }
        }
        return INT_MAX;
    }
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& qr) {
        int n = a.size();
        vector<vector<pair<int, int>>> d(n);
        int m = qr.size();
        for (int i = 0; i < m; ++i) {
            for (int j = qr[i][0]; j <= qr[i][1]; ++j) {
                d[j].push_back({i, qr[i][2]});
            }
        }
        int max_ = 0;
        vector<int> mx(n);
        for (int i = 0; i < n; ++i) {
            mx[i] = can_zero(a[i], d[i]);
            if (mx[i] == INT_MAX) {
                return -1;
            }
            max_ = max(max_, mx[i]);
        }
        return max_;
    }
};

