        int n = nums.size();
        vector<vector<int>> b(n);

        for (int i = 0; i < n; ++i) {
            b[i].assign(nums[i] + 1, 0);
            b[i][nums[i]] = 1;
        }

        bool ok = true;
        for (int i = 0; i < n; ++i) {
            if(nums[i] != 0) {
                ok = false;
            }
        }
        if (ok) {
            return 0;
        }

        for (int i = 0; i < queries.size(); ++i) {
            int l = queries[i][0];
            int r = queries[i][1];
            int v = queries[i][2];

            for (int j = l; j <= r; ++j) {
                for (int k = 0; k + v <= nums[j]; ++k) {
                    if (b[j][k + v]) {
                        b[j][k] = 1;
                    }
                }
            }
            ok = true;
            for (int j = 0; j < n; ++j) {
                if(b[j][0] == 0) {
                    ok = false;
                }
            }
            if (ok) {
                return i+1;
            }
        }
        return -1;
    }
};

