class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        auto solve = [&](int k) {
            for (int i = 0; i < n; i++) {
                bitset<1001> dp;
                dp.reset();
                dp[0] = 1;
                for (int j = 0; j < k; j++) {
                    if (queries[j][0] <= i && i <= queries[j][1]) {
                        int val = queries[j][2];
                        dp |= (dp << val);
                    }
                }
                if (!dp.test(nums[i])) return false;
            }
            return true;
        };
        if (all_of(nums.begin(), nums.end(), [](int x) { return x == 0; })) return 0;
        int left = 1, right = queries.size();
        if (!solve(right)) return -1;
        while (left < right) {
            int mid = left + (right - left) / 2;
            if (solve(mid)) right = mid;
            else left = mid + 1;
        }
        return left;
    }
};

