class Solution {
    private int[][] cache;

    private int bt(int val, int ind, int k, int[][] queries) {
        if(val == 0) return k;
        if(val < 0) return 1001;
        if(k == queries.length) return 1001;
        if(cache[val][k] != 0) return cache[val][k];
        int res = bt(val, ind, k+1, queries);
        if(queries[k][0] <= ind && ind <= queries[k][1]){
            res = Math.min(res, bt(val - queries[k][2], ind, k+1, queries));
        }
        cache[val][k] = res;
        return res;
    }

    public int minZeroArray(int[] nums, int[][] queries) {
        int[] res = new int[nums.length];
        for(int i = 0; i < nums.length; i++){
            cache = new int[nums[i]+1][queries.length];
            res[i] = bt(nums[i], i, 0, queries);
        }
        int max = 0;
        for(int n: res){
            max = Math.max(max, n);
        }
        return max == 1001? -1: max;
    }
}

