            if nums[i] == 0:
                return 0

            v = set([nums[i]])

            for j in range(len(queries)):
                start, end, val = queries[j]

                if i < start or i > end:
                    continue

                for n in list(v):
                    if n == val:
                        return j + 1
                    elif n > val:
                        v.add(n-val)

            return -1

        ans = -1
        for i in range(len(nums)):
            k = bfs(i)
            # print(i, k)
            if k == -1:
                return -1
            ans = max(ans, k)

        return ans

