// check add
class segtree {
    public:
    struct node {
        int sm = 0;
         int mn = 1e9;
int mx = -1e9;
        void apply(int l, int r, int x) {
            sm += x*(r-l+1);
            mn = min(mn, x);
            mx = max(mx, x);
        }
    };

    node unite(const node &a, const node &b) const {
        node res;
        res.sm = a.sm + b.sm;
        res.mn = min(a.mn, b.mn);
        res.mx = max(a.mx, b.mx);
        return res;
    }

    inline void push(int x, int l, int r) {
        // int y = (l + r) >> 1;
        // int z = x + ((y - l + 1) << 1);
        // if (tree[x].add != 0) { // it's wrong because of me
        //     tree[x + 1].apply(l, y, tree[x].add);
        //     tree[z].apply(y + 1, r, tree[x].add);
        //     tree[x].add = 0;
        // }
    }

    inline void pull(int x, int z) {
        tree[x] = unite(tree[x + 1], tree[z]);
    }

    int n;
    vector<node> tree;

    template <typename M>
    void build(int x, int l, int r, const vector<M> &v) {
        if (l == r) {
            tree[x].apply(l, r, v[l]);
            return;
        }
        int y = (l + r) >> 1;
        int z = x + ((y - l + 1) << 1);
        build(x + 1, l, y, v);
        build(z, y + 1, r, v);
        pull(x, z);
    }

    node query(int x, int l, int r, int LL, int rr) {
        if (LL <= l && r <= rr) {
            return tree[x];
        }
        int y = (l + r) >> 1;
        int z = x + ((y - l + 1) << 1);
        push(x, l, r);
        node res{};
        if (rr <= y) {
            res = query(x + 1, l, y, LL, rr);
        } 
        else{
            if (LL > y) {
                res = query(z, y + 1, r, LL, rr);
            } 
            else{
                res = unite(query(x + 1, l, y, LL, rr), query(z, y + 1, r, LL, rr));
            }
        }
        pull(x, z);
        return res;
    }

    template <typename... M>
    void update(int x, int l, int r, int LL, int rr, const M&... v) {
        if (LL <= l && r <= rr) {
            tree[x].apply(l, r, v...);
            return;
        }
        int y = (l + r) >> 1;
        int z = x + ((y - l + 1) << 1);
        push(x, l, r);
        if (LL <= y) {
            update(x + 1, l, y, LL, rr, v...);
        }
        if (rr > y) {
            update(z, y + 1, r, LL, rr, v...);
        }
        pull(x, z);
    }

    template <typename M>
    segtree(const vector<M> &v) {
        n = v.size();
        assert(n > 0);
        tree.resize(2 * n - 1);
        build(0, 0, n - 1, v);
    }

    node query(int LL, int rr) {
        if(LL > rr)
            return {};
        assert(0 <= LL && LL <= rr && rr <= n - 1);
        return query(0, 0, n - 1, LL, rr);
    }

    template <typename... M>
    void update(int LL, int rr, const M&... v) {
        assert(0 <= LL && LL <= rr && rr <= n - 1);
        update(0, 0, n - 1, LL, rr, v...);
    }
};

class Solution {
public:

    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        segtree st(nums);
        int m = q.size();
        int n = nums.size();
        map<int, vector<array<int, 2>>> mp;
        for(int i =0; i<m;++i){
            int l = q[i][0], r = q[i][1];
            for(int j = l; j<=r; ++j){
                mp[j].push_back({q[i][2], i+1});
            }
        }
        int ans = 0;
        for(auto&a:mp){
            int num = nums[a.first];
            vector<int> dp(num+1, 0);
            if(!num) continue;
            dp[0] = 1;
            int idx = -1;
            for(int i = 0; i<a.second.size(); ++i){
                for(int j = num; j>=a.second[i][0]; --j){
                    dp[j] |= dp[j-a.second[i][0]];

                }
                if(dp[num]){
                    idx = a.second[i][1];
                    break;
                }
            }

            if(idx==-1) return -1;
            else ans = max(ans, idx);
nums[a.first] = 0;
        }
for(int i =0 ; i<n; ++i)if(nums[i]) return -1;
        return ans;
    }
};

