class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size();
        int q=queries.size();
        unordered_map<int,vector<pair<int, int>>>applicable;
        for(int idx=0;idx<q;idx++){
            int l=queries[idx][0];
            int r=queries[idx][1];
            int val=queries[idx][2];
            for(int i=l;i<=r;i++){
                if(i>=0&&i<n){
                    applicable[i].emplace_back(idx,val);
                }
            }
        }
        int max_k=-1;
        for (int i=0;i<n;i++) {
            if(nums[i]==0)
                continue;
            auto aq=applicable[i];
            if(aq.empty())
                return -1;
            unordered_set<int>sums={0};
            bool found =false;
            int currentSum=0;
            int currentMaxIdx=-1;
            for(auto query:aq){
                int idx=query.first;
                int val=query.second;
                currentSum+=val;
                unordered_set<int>newSums;
                for(int s : sums){
                    int newS=s+val;
                    if (newS==nums[i]){
                        currentMaxIdx=max(currentMaxIdx, idx);
                        found=true;
                        break;
                    }
                    newSums.insert(newS);
                }
                if(found) 
                    break;

                for(int s:newSums){
                    sums.insert(s);
                }
                sums.insert(val);
            }
            if(!found)
                return -1;
            max_k=max(max_k,currentMaxIdx);
        }
        return max_k == -1 ? 0 : max_k + 1;
    }
};

