/**
 * @param {number[]} nums
 * @param {number[][]} queries
 * @return {number}
 */
var minZeroArray = function (nums, queries) {
  if (nums.every((x) => x === 0)) return 0;

  let n = nums.length;
  let q = queries.length;

  let cov = Array.from({ length: n }, () => Array(q).fill(0));
  for (let i = 0; i < q; i++) {
    let [l, r, val] = queries[i];
    for (let j = l; j <= r; j++) {
      cov[j][i] = val;
    }
  }

  function canSum(arr, t) {
    let dp = new Uint8Array(t + 1);
    dp[0] = 1;
    for (let x of arr) {
      for (let s = t; s >= x; s--) {
        if (dp[s - x]) dp[s] = 1;
      }
    }
    return dp[t] === 1;
  }

  for (let k = 1; k <= q; k++) {
    let ok = true;
    for (let idx = 0; idx < n; idx++) {
      let need = nums[idx];
      if (need === 0) continue;

      let arr = cov[idx].slice(0, k);
      let sumArr = arr.reduce((a, b) => a + b, 0);

      if (sumArr < need || !canSum(arr, need)) {
        ok = false;
        break;
      }
    }
    if (ok) return k;
  }
  return -1;
};

