from typing import List

class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        res = 0
        n, m = len(nums), len(queries)
        cntZero = nums.count(0)

        if cntZero == n:
            return res

        finished = set(i for i, num in enumerate(nums) if num == 0)
        sets = [set() for _ in range(n)]

        for j, (left, right, val) in enumerate(queries):
            for i in range(left, right + 1):
                if i not in finished:
                    new_set = {val}
                    for x in sets[i]:
                        new_set.add(x)
                        new_set.add(x + val)

                    if nums[i] in new_set:
                        finished.add(i)
                        if len(finished) == n:
                            return j + 1
                    else:
                        sets[i] = new_set

        return -1

