public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        auto check = [&](int k) {
            vector<vector<int>> x(n);
            int cnt = 0;
        for(auto &y: queries) {
            if(cnt >= k) break;
            cnt++;
            for(int i = y[0]; i <= y[1]; i++) x[i].push_back(y[2]);
        }
        for(int i = 0; i < n; i++) {
            if(nums[i] == 0) continue;
            vector<int> dp(nums[i] + 1, 0);
            dp[0] = 1;
            for(int j = 0; j < x[i].size(); j++) {
                for(int k = nums[i]; k >= 0; k--) {
                    if(k >= x[i][j]) dp[k] |= dp[k - x[i][j]];
                }
            }
            if(dp[nums[i]] == 0) return 0;
        }
        return 1;
        };
        int lo = 0, hi = queries.size(), ans = -1;
        while(lo <= hi) {
            int mid = (lo + hi) / 2;
            if(check(mid)) {
                hi = mid - 1;
                ans = mid;
            } else {
                lo = mid + 1;
            }
        }
        return ans;

    }
};

