class Solution 
{
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) 
    {
        int n = nums.size(), m = queries.size();

        std::vector<std::vector<std::pair<int, int>>> arr(n);
        for (int i = 0; i < m; ++i)
        {
            int left = queries[i][0];
            int right = queries[i][1];
            int val = queries[i][2];
            for (int j = left; j <= right; ++j)
                arr[j].push_back({ i, val });
        }

        int left = 0, right = m;
        while (left < right)
        {
            int mid = left + (right - left) / 2;
            if (isOk(nums, arr, mid))
                right = mid;
            else
                left = mid + 1;
        }

        if (!isOk(nums, arr, left))
            return -1;

        return left;
    }

private:
    bool isOk(
        std::vector<int>& nums, 
        std::vector<std::vector<std::pair<int, int>>>& arr,
        int k)
    {
        int n = nums.size(), m = arr.size();

        for (int i = 0; i < n; ++i)
        {
            if (nums[i] == 0)
                continue;

            std::vector<int> vals(k, 0);
            for (auto&& [i, v] : arr[i])
            {
                if (i < k)
                    vals.push_back(v);
            }

            sumBit.reset();
            sumBit[0] = 1;
            for (auto v : vals)
                sumBit |= (sumBit << v);

            if (sumBit[nums[i]] == 0)
                return false;
        }

        return true;
    }

private:
    std::bitset<1001> sumBit;
};

