        auto check=[&]()
        {
            int yes=true;
            for(int i=0;i<n;i++){
                yes&=(dp[i][0] !=-1);
            }
            return yes;
        };
        if(check()){
            return 0;
        }
        int idx=0;
        for(auto j:queries){
            idx++;
            int l=j[0], r=j[1], val=j[2];
            while(l<=r){
                getMinK(l, val, idx);
                l++;
            }
            if(check()){
                return idx;
            }
        }
        return -1;
    }
};

