class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        if (allZero(nums)) return 0;
        int left = 0, right = queries.length - 1;
        while (left <= right) {
            int mid = left + right >>> 1;
            if (valid(nums, queries, mid)) {
                right = mid - 1;
            } else {
                left = mid + 1;
            }
        }
        return left == queries.length ? -1 : left + 1;
    }

    private boolean allZero(int[] nums) {
        for (int i : nums) if (i != 0) return false;
        return true;
    }

    private boolean valid(int[] nums, int[][] queries, int idx) {
        List<List<Integer>> vals = new ArrayList<>();
        for (int i = 0; i < nums.length; i++) vals.add(new ArrayList<>());
        for (int i = 0; i <= idx; i++) {
            for (int j = queries[i][0]; j <= queries[i][1]; j++) {
                vals.get(j).add(queries[i][2]);
            }
        }
        for (int i = 0; i < nums.length; i++) {
            if (nums[i] > 0 && !canFill(nums[i], vals.get(i))) {
                return false;
            }
        }
        return true;
    }

    private boolean canFill(int val, List<Integer> list) {
        boolean[] dp = new boolean[val + 1];
        dp[0] = true;
        for (int num : list) {
            for (int i = val; i >= num; i--) {
                if (num > i) continue;
                dp[i] |= dp[i - num];
            }
        }
        return dp[val];
    }
}

