class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        N = len(nums)
        def is_possible(k):
            for i in range(N):
                if nums[i] == 0:
                    continue
                target = nums[i]

                dp = [False] * (target + 1)
                dp[0] = True

                for j in range(k):
                    left, right, val = queries[j]
                    if left <= i <= right:
                        for s in range(target,val-1,-1):
                            if dp[s-val]:
                                dp[s]=True

                if not dp[target]:
                    return False

            return True

        if not is_possible(len(queries)):
            return -1

        left, right = 0, len(queries)

        res = float('inf')
        while left <= right:
            mid = left + ((right-left)//2)
            if is_possible(mid):
                res = min(res,mid)
                right = mid - 1
            else:
                left = mid + 1

        return res

