class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        @lru_cache(maxsize=len(queries)*len(nums)*32)
        def minQueries(qi, ni, nval):
            if nval == 0:
                return 0
            if qi >= len(queries):
                return float('inf')
            take, skip = float('inf'), float('inf')
            l, r, val = queries[qi]
            if ni >= l and ni <= r and nval >= val:
                take = 1 + minQueries(qi+1, ni, nval - val)
            skip = 1 + minQueries(qi+1, ni, nval)
            return min(take, skip)

        res = 0
        for i, n in enumerate(nums):
            # print(i, n, minQueries(0, i, n))
            res = max(res, minQueries(0, i, n))
        return res if res != float('inf') else -1

