class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int l = 0, r = queries.length;
        int ans = -1;

        while (l <= r) {
            int mid = (r - l) / 2 + l;
            if (canTransform(nums, queries, mid)) {
                r = mid - 1;
                ans = mid;
            } else {
                l = mid + 1;
            }
        }

        return ans;
    }

    public boolean canTransform(int[] nums, int[][] queries, int right) {
        final int N = nums.length;
        List<List<Integer>> querySums = new ArrayList<>();

        for (int i = 0; i < N; i++) {
            querySums.add(new ArrayList<>());
        }

        for (int i = 0; i < right; i++) {
            int start = queries[i][0];
            int end = queries[i][1];
            int decrement = queries[i][2];

            for (int j = start; j <= end; j++) {
                querySums.get(j).add(decrement);
            }
        }

        for (int i = 0; i < N; i++) {
            if (!canMakeExactSum(querySums.get(i), nums[i])) {
                return false;
            }
        }

        return true;
    }

    private boolean canMakeExactSum(List<Integer> options, int target) {
        if (target == 0) return true;

        Set<Integer> possibleSums = new HashSet<>();
        possibleSums.add(0);

        for (int num : options) {
            Set<Integer> newSums = new HashSet<>(possibleSums);
            for (int sum : possibleSums) {
                int newSum = sum + num;
                if (newSum == target) return true;
                newSums.add(newSum);
            }
            possibleSums = newSums;
        }

        return possibleSums.contains(target);
    }

}

