import java.util.*;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        BitSet[] arr = new BitSet[n];
        for (int j = 0; j < n; j++) {
            arr[j] = new BitSet(1001);
            arr[j].set(0);
        }
        int flag = 1;
        for (int j = 0; j < n; j++) {
            if (nums[j] != 0) {
                flag = 0;
                break;
            }
        }
        if (flag == 1) return 0;
        for (int k = 0; k < queries.length; k++) {
            int li = queries[k][0];
            int ri = queries[k][1];
            int val = queries[k][2];
            for (int j = li; j <= ri; j++) {
                BitSet temp = new BitSet(1001);
                for (int i = 0; i < 1001 - val; i++) {
                    if (arr[j].get(i)) {
                        temp.set(i + val);
                    }
                }
                arr[j].or(temp);
                for (int pos = nums[j] + 1; pos < 1001; pos++) {
                    arr[j].clear(pos);
                }
            }
            flag = 1;
            for (int j = 0; j < n; j++) {
                if (!arr[j].get(nums[j])) {
                    flag = 0;
                    break;
                }
            }
            if (flag == 1) return k + 1;
        }
