class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();

        bool allZero = true;
        for (int num : nums) {
            if (num != 0) {
                allZero = false;
                break;
            }
        }
        if (allZero) {
            return 0;
        }

        vector<vector<bool>> possible(n);
        for (int j = 0; j < n; ++j) {
            possible[j].resize(nums[j] + 1, false);
            possible[j][0] = true;
        }

        for (int k = 0; k < m; ++k) {
            int li = queries[k][0];
            int ri = queries[k][1];
            int vali = queries[k][2];

            for (int j = li; j <= ri; ++j) {
                int max_s = nums[j];
                for (int s = max_s; s >= vali; --s) {
                    if (s - vali >= 0) {
                        possible[j][s] = possible[j][s] || possible[j][s - vali];
                    }
                }
            }

            bool can_achieve = true;
            for (int j = 0; j < n; ++j) {
                if (!possible[j][nums[j]]) {
                    can_achieve = false;
                    break;
                }
            }
            if (can_achieve) {
                return k + 1;
            }
        }

        return -1;
    }
};

