class Solution {
public:

    bool subsetSum(vector<int> &temp, int i, int target){
        if(target==0) return true;
        if(i==temp.size()) return false;
        bool Ans = false;
        if(target-temp[i] >= 0)
            Ans = subsetSum(temp, i+1, target-temp[i]);
        if(Ans) return Ans;
        Ans = Ans | subsetSum(temp, i+1, target);
        return Ans;
    }

    bool subsetSum_bitset(vector<int>& nums, int target) {
        bitset<100001> dp; 
        dp[0] = 1; 
        for (int num : nums) {
            dp |= dp << num; 
        }
        return dp[target];
    }

    bool canSolve(vector<int>&A, vector<vector<int>>&Q, int mid){
        unordered_map<int, vector<int>> M;
        for(int i=0; i<=mid; i++){
            int l = Q[i][0];
            int r = Q[i][1];
            int val = Q[i][2];
            for(int j=l; j<=r; j++){
                if(val <= A[j] ){
                     M[j].push_back(val);
                }

            }
        }

        for(int i=0; i<A.size(); i++){
            if(A[i]==0) continue;
            bool subsetAns =  subsetSum_bitset(M[i], A[i]);
            if(!subsetAns) return false;
        }

        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        vector<int> temp =  nums;
        sort(temp.begin(), temp.end());
        if(temp.back() == 0) return 0;

        int start = 0, end = queries.size()-1;
        while(start < end){
            int mid = start + (end-start)/2;
            if(canSolve(nums, queries, mid)){
                end = mid;
            } else{
                start = mid+1;
            }
        }

        if(canSolve(nums, queries, start)) return start+1;
        return -1;
    }
};

