class Solution {
public:
    bool help(vector<int>& arr,int target){
        vector<bool> dp(target+1,false);
        dp[0]=true;
        for(auto val:arr){
            for(int s=target;s>=val;s--) dp[s]=dp[s]||dp[s-val];
        }

        return dp[target];
    }
    bool f(vector<int>& nums,vector<vector<int>>& queries,vector<vector<int>>& temp,int mid){
        for(int i=0;i<nums.size();i++){
            if(nums[i]==0) continue;
            vector<int> avail;
            for(int j=0;j<temp[i].size();j++){
                if(temp[i][j]<mid){
                    avail.push_back(queries[temp[i][j]][2]);
                }
            } 
            if(!help(avail,nums[i])) return false;
        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        vector<vector<int>> temp(nums.size());
        for(int i=0;i<queries.size();i++){
            for(int j=queries[i][0];j<=queries[i][1];j++) temp[j].push_back(i);
        }
        int lo=0,hi=queries.size(),ans=-1;
        while(lo<=hi){
            int mid=(lo+hi)/2;
            if(f(nums,queries,temp,mid)){
                ans=mid;
                hi=mid-1;
            }
            else lo=mid+1;
        }
        return ans;
    }
};

