class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
    int n = nums.size();

    vector<unordered_set<int>> achievable(n);

    for (int i = 0; i < n; i++) {
        achievable[i].insert(0);
    }

    if (all_of(nums.begin(), nums.end(), [](int x) { return x == 0; })) {
        return 0;
    }

    for (int queryIdx = 0; queryIdx < queries.size(); queryIdx++) {
        int left = queries[queryIdx][0];
        int right = queries[queryIdx][1];
        int addValue = queries[queryIdx][2];

        for (int pos = left; pos <= right; pos++) {
            unordered_set<int> newValues;

            for (int val : achievable[pos]) {
                int newVal = val + addValue;
                if (newVal <= 1000) {  
                    newValues.insert(newVal);
                }
            }

            achievable[pos].insert(newValues.begin(), newValues.end());
        }

        bool allTargetsAchievable = true;
        for (int i = 0; i < n; i++) {
            if (achievable[i].find(nums[i]) == achievable[i].end()) {
                allTargetsAchievable = false;
                break;
            }
        }

        if (allTargetsAchievable) {
            return queryIdx + 1;
        }
    }

    return -1;
}
};

