#include <vector>

#include <bitset>

using namespace std;

class Solution {

public:

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {

        int n = nums.size();

        vector<bitset<1001>> dp(n);

        // Initialize dp: each index starts with only '0' reachable

        for (int j = 0; j < n; j++) {

            dp[j].reset();

            dp[j].set(0);

        }

        // Check if all elements in nums are zero initially

        bool allZero = true;

        for (int j = 0; j < n; j++) {

            if (nums[j] != 0) {

                allZero = false;

                break;

            }

        }

        if (allZero) return 0;

        // Process Queries

        for (int k = 0; k < queries.size(); k++) {

            int l = queries[k][0];

            int r = queries[k][1];

            int v = queries[k][2];

            // Apply updates to dp array

            for (int j = r; j >= l; j--) {

                dp[j] |= (dp[j] << v);

            }

            // Reset numbers that exceed the current number in nums

            for (int j = l; j <= r; j++) {

                for (int pos = nums[j] + 1; pos < 1001; pos++) {

                    dp[j].reset(pos);

                }

            }

            // Check if all numbers can be reduced to zero

            bool allAchieved = true;

            for (int j = 0; j < n; j++) {

                if (!dp[j].test(nums[j])) {

                    allAchieved = false;

                    break;

                }

            }

            if (allAchieved) return k + 1; // Return the query count

        }

        return -1; // Not possible to reach zero for all elements

    }

};

