            }
        }

        if (isAllZero) return 0;

        for (int step = 0; step < actions.size(); step++) {
            int start = actions[step][0];
            int end = actions[step][1];
            int increment = actions[step][2];

            for (int idx = start; idx <= end; idx++) {
                tracker[idx] |= (tracker[idx] << increment);
                for (int pos = elements[idx] + 1; pos < 1001; pos++) {
                    tracker[idx].reset(pos);
                }
            }

            bool conditionMet = true;
            for (int idx = 0; idx < size; idx++) {
                if (!tracker[idx].test(elements[idx])) {
                    conditionMet = false;
                    break;
                }
            }

            if (conditionMet) return step + 1;
        }

        return -1;

    }
};

