class Solution {
public:
    bool check(vector<int>& nums, vector<vector<int>>& queries, int qidx, int n, int nmax) {
        vector<vector<int>> ops(n+1);
        for(int i = 0; i <= qidx; i++) {
            vector<int> q = queries[i];
            ops[q[0]].push_back(q[2]);
            ops[q[1]+1].push_back(-q[2]);
        }
        map<int, int> left;
        for(int i = 0 ; i < n; i++) {
            for(int q : ops[i]) {
                if(q > 0) {
                    left[q] += 1;
                } else {
                    left[-q] -= 1;
                }
            }
            // for(auto ele : left) {
            //     cout << ele.first<< " " << ele.second << endl;
            // }
            if(nums[i] == 0) continue;
            vector<bool> avail(nmax + 1, false);
            avail[0] = true;
            for(auto kv : left) {
                int k = kv.first;
                int v = kv.second;
                for(int j = 0; j < v; j++) {
                    vector<bool> newavail = avail;
                    for(int num = 0; num <= nmax; num++) {
                        if(!avail[num]) continue;
                        int newn = num + k;
                        if(newn <= nmax){
                            newavail[newn] = true;
                            // cout << newn << endl;
                        }
                    }
                    avail = newavail;
                    if(avail[nums[i]]) break;
                }
            }
            if(!avail[nums[i]]) return false;
        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();  
        int qmax = queries.size();
        int nmax = 0;
        for(int num : nums) {
            nmax = max(nmax, num);
        }
        if(nmax == 0) return 0; 
        // cout << nmax << endl;
        bool resp = check(nums, queries, qmax-1, n, nmax);
        if(!resp) return -1;
        resp = check(nums, queries, 0, n, nmax);
        if(resp) return 1;
        int le = 0;
        int ri = qmax - 1;
        while(le < ri - 1) {
            int mid = (le + ri) / 2;
            bool r = check(nums, queries, mid, n, nmax);
            if(r){
                ri = mid;
            } else {
                le = mid;
            }
        }
        return ri + 1;
    }
};

