#define ll long long

class Solution {
public:
    int minZeroArray(vector<int>& v, vector<vector<int>>& q) {
        ll n = v.size();
        ll m = q.size();

        auto isposs = [&](ll idx) -> bool {

            vector< vector<int> > whatAll(n);
            for(int i=0;i<=idx;i++){
                vector<int> x = q[i];
                int l = x[0];
                int r = x[1];
                int val = x[2];
                for(int i=l;i<=r;i++) whatAll[i].push_back(val);
            }

            auto canSum = [&](ll s, vector<int> &op) -> bool {
                bitset<10001> dp;
                dp[0] = 1;
                for (auto &x:op) dp |= (dp << x);
                return dp[s];
            };

            bool ok = true;
            for(int i=0;i<n;i++) ok &= canSum(v[i], whatAll[i]);
            return ok;
        };

        bool allZero = true;
        for(auto &x:v) allZero &= (x == 0);
        if(allZero) return 0;

        int l = 0, r = m-1;
        while(l+1<r)
        {
            int md = (r-l)/2 + l;
            if( isposs(md) ) r = md;
            else l = md;
        }

        int ans = -1;
        if( isposs(r) ) ans = r;
        if( isposs(l) ) ans = l;
        return (ans != -1 ? ans + 1 : ans);
    }
};

