class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int q = queries.size();
        int ct = 0;
        vector<int> hash(n, 0);
        int prev=0;
        for (int i = 0; i < n; i++) {
            if (hash[i]) continue;
            if (nums[i] == 0) continue;

            ct++;
            vector<int> temp;

            for (int j = 0; j < q; j++) {
                if (queries[j][0] <= i && queries[j][1] >= i)
                    temp.push_back(queries[j][2]);


            int m = temp.size();
            sort(temp.begin(), temp.end());

            vector<bool> dp(nums[i] + 1, false);
            dp[0] = true;

            for (int j = 0; j < m; j++) {
                for (int val = nums[i]; val >= temp[j]; val--) {
                    if (dp[val - temp[j]])
                        dp[val] = true;
                }
            }

            if (dp[nums[i]]) {
                hash[i] = 1;
                prev=max(prev,j+1);
                break;
            }
        }
        }
        for (int i = 0; i < n; i++) {
            if (nums[i] != 0 && !hash[i])
                return -1;
        }

        return prev;
    }
};

