class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        bool already = true;
        for (int x : nums) {
            if (x != 0) { 
                already = false; 
                break;
            }
        }
        if (already) return 0;

        vector<vector<pair<int, int>>> affects(n); // idx -> qidx, val
        for (int j = 0; j < m; j++) {
            int l = queries[j][0], r = queries[j][1], val = queries[j][2];
            for (int i = l; i <= r; i++) {
                affects[i].push_back({j, val});
            }
        }
        for (int i = 0; i < n; i++) {
            sort(affects[i].begin(), affects[i].end(), [](auto &a, auto &b) {
                return a.first < b.first;
            });
        }

        auto OK = [&](int k) -> bool {
            for (int i = 0; i < n; i++) {
                int target = nums[i];
                if (target == 0) continue;

                bitset<1100> dp;
                dp[0] = 1;
                for (auto [qid, val] : affects[i]) {
                    if (qid + 1 > k) break;
                    dp |= (dp << val);
                    if (dp[target]) break;
                }
                if (!dp[target]) return false;
            }
            return true;
        };
        int l = 1, r = m;
        while (l < r) {
            int mid = l + r >> 1;
            if (OK(mid))
                r = mid;
            else 
                l = mid + 1;
        }
        return OK(l) ? l : -1;
    }
};

