class Solution {
public:
    vector<vector<vector<int>>> dp;
    int solve(int curr,int idx, vector<vector<int>>& queries, int n,int i){
        if(curr==0) return i;
        if(i>=n) return INT_MAX;

        if (dp[idx][curr][i] != -1) return dp[idx][curr][i];

        int notPick = solve(curr,idx,queries,n,i+1);
        int pick = INT_MAX;
        if(idx>=queries[i][0] && idx<=queries[i][1] && curr >= queries[i][2]) pick = solve(curr-queries[i][2],idx,queries,n,i+1);

        return dp[idx][curr][i] = min(pick,notPick);
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n =nums.size(),q = queries.size();
        int ans = 0;
        int maxVal = *max_element(nums.begin(), nums.end());
        dp.resize(n, vector<vector<int>>(maxVal + 1, vector<int>(q + 1, -1)));
        for(int i=0;i<n;i++){
            int k = solve(nums[i],i,queries,q,0);
            if(k==INT_MAX) return -1;
            ans = max(k,ans);
        }
        return ans;
    }
};

